document.addEventListener("DOMContentLoaded",()=>{const t=document.getElementById("main-header");window.addEventListener("scroll",()=>{window.scrollY>50?t.classList.add("scrolled"):t.classList.remove("scrolled")});const n=document.getElementById("menuToggle"),c=document.getElementById("closeMenu"),e=document.getElementById("overlayMenu"),s=document.querySelectorAll(".overlay-link");n.addEventListener("click",()=>{e.classList.add("active")}),c.addEventListener("click",()=>{e.classList.remove("active")}),s.forEach(d=>{d.addEventListener("click",()=>{e.classList.remove("active")})})});
